$(function(){var n=$("#modal-placeholder");$('button[data-toggle="ajax-modal"]').click(function(){var t=$(this).data("url");$.get(t).done(function(t){n.html(t);n.find(".modal").modal("show")})});n.on("click",'[data-save="modal"]',function(t){t.preventDefault();var i=$(this).parents(".modal").find("form"),r=i.attr("action"),u=i.serialize();$.post(r,u).done(function(t){var i=$(".modal-body",t);n.find(".modal-body").replaceWith(i)})})});